import { useState } from "react";
import { Signup } from "./inscription";
import { Login } from "./login";

export default function Accueil() {
  const [page, setPage] = useState("home");

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-8">
      {page === "home" && <Home goLogin={() => setPage("login")} goSignup={() => setPage("signup")} />}
      {page === "login" && <Login goSignup={() => setPage("signup")} />}
      {page === "signup" && <Signup goLogin={() => setPage("login")} />}
    </div>
  );
}

function Home({ goLogin, goSignup }) {
  return (
    <div className="bg-white shadow-xl p-10 rounded-2xl w-full max-w-md text-center">
      <h1 className="text-3xl font-bold mb-6">Bienvenue</h1>
      <p className="mb-6 text-gray-600">Veuillez choisir une action :</p>

      <button
        onClick={goLogin}
        className="w-full p-3 bg-blue-600 text-white rounded-xl font-semibold mb-3"
      >
        Login
      </button>

      <button
        onClick={goSignup}
        className="w-full p-3 bg-gray-200 text-gray-800 rounded-xl font-semibold"
      >
        Sign Up
      </button>
    </div>
  );
}
