import { useState, useEffect } from "react";
import "./weather.css";

/* ------------------------------------------------------------------ */
/*                 COMPOSANT : HEADER AVEC L’HEURE                    */
/* ------------------------------------------------------------------ */
function ClockHeader() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = time.getHours();
  const minutes = time.getMinutes();
  const displayHour = hours % 12 || 12;
  const ampm = hours >= 12 ? "PM" : "AM";
  const formattedMinutes = minutes.toString().padStart(2, "0");

  const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  const months = ["January","February","March","April","May","June",
                  "July","August","September","October","November","December"];

  return (
    <div className="card">
      <p className="day-text">
        {days[time.getDay()]}, {months[time.getMonth()]} {time.getDate()}
      </p>

      <p className="time-text">
        {displayHour}:{formattedMinutes}
        <span className="time-sub-text">{ampm}</span>
      </p>

      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 16 16"
        className="moon"
      >
        <path d="M6 .278a.768.768 0 0 1 .08.858..."></path>
      </svg>
    </div>
  );
}

/* ------------------------------------------------------------------ */
/*                 COMPOSANT : RECHERCHE MÉTÉO                       */
/* ------------------------------------------------------------------ */
function WeatherSearch() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);

  const handleSearch = (e) => {
    e.preventDefault();

    // Fake data – remplacée plus tard par ta BD
    const fake = {
      temp: 32,
      realFeel: 39,
      realFeelShade: 36,
      wind: "S 20 km/h",
      gusts: "20 km/h",
      airQuality: "Correct",
      description: "Partiellement ensoleillé",
    };

    setWeather(fake);
  };

  return (
    <div className="weather-page">

      <form className="weather-form" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Entrez votre ville..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button type="submit">Rechercher</button>
      </form>

      {weather && (
        <div className="weather-card">

          <div className="weather-left">
            <img src="/weather-icons/sun-cloud.png" alt="" className="weather-icon" />

            <div>
              <div className="temp">{weather.temp}°</div>
              <div className="realfeel">RealFeel {weather.realFeel}°</div>
              <div className="desc">{weather.description}</div>
            </div>
          </div>

          <div className="weather-right">
            <div className="row"><span>RealFeel Shade</span> <b>{weather.realFeelShade}°</b></div>
            <div className="row"><span>Vent</span> <b>{weather.wind}</b></div>
            <div className="row"><span>Rafales</span> <b>{weather.gusts}</b></div>
            <div className="row"><span>Qualité de l'air</span> <b className="good">{weather.airQuality}</b></div>
          </div>

        </div>
      )}
    </div>
  );
}

/* ------------------------------------------------------------------ */
/*                     COMPOSANT PRINCIPAL EXPORTÉ                    */
/* ------------------------------------------------------------------ */
export default function WeatherCard() {
  return (
    <>
      <ClockHeader />
      <WeatherSearch />
    </>
  );
}
